package selfProgramming;

public class Promotion {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int count = 12; 
		double sum = 490.27;
		double result =  sum / count; /* Data type for count is integer
		 							                 so because of count 
		 							                 will be divide with sum which is double 
		                               so count data type will be change to float 
		                               this is call promotion*/
		
		System.out.print (result);
	}

}
